import React from 'react';
import './App.css';
// =============================================================================
// いいねボタン-
// =============================================================================
function LikeButton(){
  // いいねの状態をstate管理。setLikeが変更されると画面が更新される
  const [liked, setLike] = React.useState(false);

  // クリック時の処理。アロー関数
  const clicked = () => {

    // 現在のlikedの状態と逆のboolean型をchangeに代入
    const change = !liked;
    // setLikeの更新。画面が更新される。changeを代入
    setLike(change);
  }

  function LikeText(props: any){
    return <p>いいね数：</p>
  }

  // 三項演算子
  // likedがtlueの場合はclassがlikedに
  const className = liked?'liked':'like';
  // ボタンを生成
  return <button className={className + ' likeBtn'} onClick={()=> clicked()}>Like</button>
}

// 描画----------------------------------------
function App() {
  return (
    <div className="App">
      <LikeButton />
      <LikeText />
    </div>
  );
}

export default App;
