import React from 'react';
import './App.css';

// いいねボタンコンポーネント
function LikeButton(){
  // いいねの状態をstate管理。setLikeが変更されると画面が更新される
  const [liked, setLike] = React.useState(false);

  // クリック時の処理。アロー関数
  const clicked = () => {

    // 現在のlikedの状態と逆のboolean型をchangeに代入
    const change = !liked;
    // setLikeの更新。画面が更新される。changeを代入
    setLike(change);
  }

  // 三項演算子
  // likedがtlueの場合はclassがlikeに
  const className = liked?'liked':'like';
  // ボタンを生成
  return <button className={className + ' likeBtn'} onClick={()=> clicked()}>Like</button>
}

// 描画----------------------------------------
function App() {
  return (
    <div className="App">
      <LikeButton />
    </div>
  );
}

export default App;
